DROP TABLE IF EXISTS hooks;
CREATE TABLE hooks (
  hooks_id INT NOT NULL AUTO_INCREMENT,
  hooks_path VARCHAR(255) NOT NULL,
  hooks_site VARCHAR(63) NOT NULL,
  hooks_group VARCHAR(63) NOT NULL,
  hooks_action VARCHAR(255) NOT NULL,
  hooks_code VARCHAR(127) NOT NULL,
  hooks_class VARCHAR(255) NOT NULL,
  hooks_method VARCHAR(255) NOT NULL,
  PRIMARY KEY (hooks_id),
  KEY idx_hooks_site_group (hooks_site, hooks_group)
) CHARACTER SET utf8 COLLATE utf8_unicode_ci; 

delete from configuration where configuration_key = "MAX_DISPLAY_MANUFACTURERS_IN_A_LIST";
delete from configuration where configuration_key = "MAX_MANUFACTURERS_LIST";
delete from configuration where configuration_key = "MAX_DISPLAY_MANUFACTURER_NAME_LEN";
delete from configuration where configuration_key = "MAX_DISPLAY_NEW_REVIEWS";


delete from configuration where configuration_key LIKE "MODULE_ADMIN_DASHBOARD_%";

